import React, { useState } from 'react';

const StockManagementSignUpForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    gender: '',
    country: '',
    address: '',
    mobile: '', // Added mobile number to the state
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Sign-up form submitted:', formData);
  };

  const formStyles = {
   
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    padding: '20px',
    borderRadius: '8px',
    maxWidth: '400px',
    margin: 'auto',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
  };

  const labelStyles = {
    display: 'block',
    marginBottom: '8px',
    color: '#333',
    
  };

  const inputStyles = {
    width: '100%',
    padding: '10px',
    marginBottom: '12px',
    boxSizing: 'border-box',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '14px',
  };

  const buttonStyles = {
    background: '#4CAF50',
    color: 'white',
    padding: '12px 15px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px',
  };

  return (
    <form onSubmit={handleSubmit} style={formStyles}>
      <label style={labelStyles}>
        First Name:
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleInputChange}
          style={inputStyles}
        />
      </label>
      <label style={labelStyles}>
        Last Name:
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleInputChange}
          style={inputStyles}
        />
      </label>
      <label style={labelStyles}>
        Email:
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          style={inputStyles}
        />
      </label>
      <label style={labelStyles}>
        Gender:
        <select
          name="gender"
          value={formData.gender}
          onChange={handleInputChange}
          style={inputStyles}
        >
          <option value="">Select</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>
      </label>
      <label style={labelStyles}>
        Mobile:
        <input
          type="tel"
          name="mobile"
          value={formData.mobile}
          onChange={handleInputChange}
          style={inputStyles}
        />
      </label>
      <label style={labelStyles}>
        Country:
        <input
          type="text"
          name="country"
          value={formData.country}
          onChange={handleInputChange}
          style={inputStyles}
        />
      </label>
      <label style={labelStyles}>
        Address:
        <textarea
          name="address"
          value={formData.address}
          onChange={handleInputChange}
          style={inputStyles}
        />
      </label>
      <button type="submit" style={buttonStyles}>
        Sign Up
      </button>
    </form>
  );
};

export default StockManagementSignUpForm;
